button_text_sm.script:
function onButtonPress()
local w = Interface.findWindow("reference_groupedlist", "reference.skill");
if w then
Interface.toggleWindow("reference_groupedlist", "reference.skill");
else
w = Interface.openWindow("reference_groupedlist", "reference.skill");
w.init({ sRecordType = "ability", sListView = "skill" });
end
end

